import * as React from 'react';
import { View, Text } from 'react-native';
import { useState, useEffect} from 'react';
import axios from 'axios'
export default function aboutScreen({ navigation }) {
    const [posts,setPosts]=useState([])
    const [u,setu]=useState({ufname:'',ulname:'',uDepartment:''})
    const fetchData=()=>{
      axios.get('http://127.0.0.1:8000/teach/ListTeach/')
      .then((Response)=> setPosts(Response.data))
      .catch((err)=>console.log(err))
    }
    const deleteData=(id)=>{
        axios.delete(`http://127.0.0.1:8000/teach/deletTeach/${id}`)
        .then((Response)=> alert('successfully deleted'),fetchData())
        .catch((err)=>console.log(err))
      }
      const setData=(Id)=>{
        axios.patch('http://127.0.0.1:8000/teach/ListTeach/',{fname:ufname},{lname:ulname},{department:uDepartment}) 
        .then((res)=> {alert('Updated Successfully');fetchData();})

        .catch((err)=>alert('Failed'))
      }
    useEffect(()=>{fetchData()},[])
    return (

        <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
           { posts.map((post)=>{
      return (
        <Text
                onPress={() => navigation.navigate('Home')}
                style={{ fontSize: 26, fontWeight: 'bold' }}>
                    First Name: {post.fname} <br></br>
                    Last Name:{post.lname} <br></br> 
                    Department: {post.department}<br></br>
                    <button onClick={()=>deleteData(post.id)}>Delete</button>
        </Text>
      ) 
    })} 
     <input onChange={(e)=>setu({ufname: e.target.value})} type='text'/>
     <input onChange={(e)=>setu({ulname: e.target.value})} type='text'/>
     <input onChange={(e)=>setu({uDepartment: e.target.value})} type='text'/>
     <button onClick={()=>setData()}>Add new</button>
        </View>
    );
}
